import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";

const Expenses = (props) => {
  const [expensesList, setExpensesList] = useState(props.items);
  const [year, setYear] = useState("2020");

  // const [updatedExpense, setUpdatedExpense] = useState(props.expense);

  // setUpdatedExpense({
  //   expense: props.expense
  // })

  // console.log(props.items);

  const expenseFilterChangeHandler = (year) => {
    console.log("in Expenses");
    console.log(year);
    setYear(year);
    const filteredExpensesList = props.items.filter(
      (item) => item.date.getFullYear() === year
    );
    console.log("filteredList");
    console.log(filteredExpensesList);
  };

  // const expense = props.expense;
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          selected={year}
          onFilterChange={expenseFilterChangeHandler}
        />
        {props.items.map((item) => (
          <ExpenseItem
            key={item.id}
            title={item.title}
            amount={item.amount}
            date={item.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
