import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
    console.log(enteredTitle);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    console.log(enteredAmount);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    console.log("submitted !!!!");

    const eneteredDetails = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    console.log(eneteredDetails);
    props.onSaveExpenseData(eneteredDetails);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
    setformType("AddExpenseForm");
  };

  const submitAddExpenseHandler = (event) => { 
    event.preventDefault();
    setformType("");
  }

  const cancelHandler = (event) => {
    event.preventDefault();
    console.log("cancelled !!!!");
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
    setformType("AddExpenseForm");
  };

  if(formType === "AddExpenseForm") {
    return (
      <form onSubmit={submitAddExpenseHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__actions">
            <button type="submit">Add New Expense</button>
          </div>
        </div>
      </form>
    );
  }
 else {
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input value={enteredAmount}
            type="number"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>date</label>
          <input value={enteredDate}
            type="date"
            min="2019-01-01"
            max="2021-12-31"
            onChange={dateChangeHandler}
          />
        </div>
        <div className="new-expense__actions">
          <button onClick={cancelHandler}>Cancel</button>
          <button type="submit">Add Expense</button>
        </div>
      </div>
    </form>
  );
 }


};

export default ExpenseForm;
